// Configuration
const CONFIG = {
  API_VERSION: "v2",
  CLIENT: "typingmind",
  CLIENT_VERSION: "4",
  CACHE_DURATION_MS: 60000, // 1 minute default
  DEFAULT_API_URL: "DEFAULT_API_URL" // Fallback if not in settings
};

// Simple memory cache
const memoryCache = {
  data: {},

  set(key, value, ttl = CONFIG.CACHE_DURATION_MS) {
    this.data[key] = {
      value,
      expiry: Date.now() + ttl
    };
  },

  get(key) {
    const item = this.data[key];
    if (!item) return null;

    if (Date.now() > item.expiry) {
      delete this.data[key];
      return null;
    }

    return item.value;
  },

  invalidate(key) {
    delete this.data[key];
  },

  invalidateAll() {
    this.data = {};
  }
};

// Debug logging helper
function log(message, data, userSettings) {
  // Always log to console for developer debugging
  console.log(`[Memory Box] ${message}`, data);

  // Only return debug info to the user if debug mode is enabled
  if (userSettings && userSettings.debug === 'on') {
    return `DEBUG: ${message} ${JSON.stringify(data, null, 2)}`;
  }

  return null;
}

// Helper function to get the memory endpoint URL
function getMemoryEndpoint(userSettings) {
  // Use API URL from settings or fall back to default
  const apiBase = userSettings.apiUrl || CONFIG.DEFAULT_API_URL;

  // Ensure the URL ends with a slash
  const baseUrl = apiBase.endsWith('/') ? apiBase : apiBase + '/';

  // Construct the full endpoint URL
  return `${baseUrl}${CONFIG.API_VERSION}/memory?client=${CONFIG.CLIENT}&version=${CONFIG.CLIENT_VERSION}`;
}

// API request helper
async function apiRequest(path, options, userSettings) {
  const debugInfo = [];
  const requestDebug = log('API Request:', { url: path, method: options.method }, userSettings);
  if (requestDebug) debugInfo.push(requestDebug);

  try {
    // Check if caching is enabled
    const isCacheEnabled = userSettings.cacheEnabled !== 'off';

    // For GET requests, check cache first
    if (isCacheEnabled && options.method === 'GET') {
      const cacheKey = `${path}_${JSON.stringify(options.headers)}`;
      const cachedData = memoryCache.get(cacheKey);

      if (cachedData) {
        const cacheDebug = log('Using cached data:', { path }, userSettings);
        if (cacheDebug) debugInfo.push(cacheDebug);

        return {
          ...cachedData,
          _debug: debugInfo.length > 0 ? debugInfo : undefined
        };
      }
    }

    // Set up abort controller for timeout
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout

    // Make the request
    const response = await fetch(path, {
      ...options,
      signal: controller.signal
    });

    // Clear the timeout
    clearTimeout(timeoutId);

    // Handle errors
    if (!response.ok) {
      const errorText = await response.text();
      const errorDebug = log('API Error:', { status: response.status, error: errorText }, userSettings);
      if (errorDebug) debugInfo.push(errorDebug);

      throw new Error(`API error (${response.status}): ${errorText}`);
    }

    // Parse the response
    const data = await response.json();
    const responseDebug = log('API Response:', { status: response.status, dataSize: JSON.stringify(data).length }, userSettings);
    if (responseDebug) debugInfo.push(responseDebug);

    // Cache the result for GET requests
    if (isCacheEnabled && options.method === 'GET') {
      const cacheKey = `${path}_${JSON.stringify(options.headers)}`;
      memoryCache.set(cacheKey, data);
    }

    // Add debug info if available
    if (debugInfo.length > 0) {
      return {
        ...data,
        _debug: debugInfo
      };
    }

    return data;
  } catch (error) {
    // Handle timeout errors
    if (error.name === 'AbortError') {
      const timeoutDebug = log('API Timeout:', { timeout: 10000 }, userSettings);
      if (timeoutDebug) debugInfo.push(timeoutDebug);

      throw new Error('Request timed out. The Memory Box server took too long to respond.');
    }

    // Log other errors
    const errorDebug = log('API Error:', { message: error.message }, userSettings);
    if (errorDebug) debugInfo.push(errorDebug);

    // Re-throw with debug info
    const enhancedError = new Error(error.message);
    enhancedError._debug = debugInfo;
    throw enhancedError;
  }
}

// Format memory with date if needed
function formatMemory(memory, bucketId, userSettings) {
  log('Formatting memory:', { length: memory.length, bucketId }, userSettings);

  // Check if memory already has a date prefix
  if (!memory.match(/^\d{4}-\d{2}-\d{2}/)) {
    const today = new Date();
    const formattedDate = today.toISOString().split("T")[0];
    memory = `${formattedDate}\n\n${memory}`;
    log('Added date prefix:', { date: formattedDate }, userSettings);
  }

  return memory;
}

async function queryMemories(text, userSettings) {
  log('Searching memories:', { query: text }, userSettings);

  try {
    const endpoint = getMemoryEndpoint(userSettings);
    const url = new URL(endpoint);
    url.searchParams.append('query', text);
    url.searchParams.append('v', 2);

    const memories = await apiRequest(url.toString(), {
      method: "GET",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${userSettings.token}`
      }
    }, userSettings);

    log('Search results:', { count: memories.length }, userSettings);

    return memories;
  } catch (error) {
    log('Search error:', { message: error.message }, userSettings);

    return [];
  }
}

async function fetchAllMemories(userSettings) {
  log('Fetching all memories', {}, userSettings);

  try {
    const endpoint = getMemoryEndpoint(userSettings);
    const url = new URL(endpoint);
    url.searchParams.append('all', 'true');
    url.searchParams.append('v', 2);

    const memories = await apiRequest(url.toString(), {
      method: "GET",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${userSettings.token}`
      }
    }, userSettings);

    log('Fetched all memories:', { count: memories.length }, userSettings);

    return memories;
  } catch (error) {
    log('Error fetching all memories:', { message: error.message }, userSettings);

    return [];
  }
}

async function fetchMemoriesFromBucket(userSettings, bucketId) {
  // Use provided bucketId or fall back to settings
  const bucketToUse = bucketId || userSettings.bucket;

  // If no bucket is available, return an error
  if (!bucketToUse) {
    log('No bucket specified', {}, userSettings);

    return [];
  }

  log('Fetching memories from bucket:', { bucket: bucketToUse }, userSettings);

  try {
    const endpoint = getMemoryEndpoint(userSettings);
    const url = new URL(endpoint);
    url.searchParams.append('bucketId', bucketToUse);

    const memories = await apiRequest(url.toString(), {
      method: "GET",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${userSettings.token}`
      }
    }, userSettings);

    log('Fetched bucket memories:', { bucket: bucketToUse, count: memories.length }, userSettings);

    return memories;
  } catch (error) {
    log('Error fetching bucket memories:', { bucket: bucketToUse, message: error.message }, userSettings);

    return [];
  }
}

async function addMemory(memory, bucketId, userSettings) {
  // Use provided bucketId or fall back to settings
  const bucketToUse = bucketId || userSettings.bucket;

  // Format the memory with date if needed
  const formattedMemory = formatMemory(memory, bucketToUse, userSettings);

  log('Adding memory:', { length: formattedMemory.length, bucket: bucketToUse }, userSettings);

  try {
    const endpoint = getMemoryEndpoint(userSettings);

    const newMemo = { text: formattedMemory };
    if (bucketToUse) {
      newMemo.bucketId = bucketToUse;
    }

    const result = await apiRequest(endpoint, {
      method: "POST",
      mode: "cors",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${userSettings.token}`
      },
      body: JSON.stringify(newMemo)
    }, userSettings);

    log('Memory added successfully:', { id: result.id }, userSettings);

    // Invalidate relevant caches
    if (userSettings.cacheEnabled !== 'off') {
      const cacheKey1 = `${endpoint}_all=true`;
      const cacheKey2 = bucketToUse ? `${endpoint}_bucketId=${bucketToUse}` : null;

      memoryCache.invalidate(cacheKey1);
      if (cacheKey2) memoryCache.invalidate(cacheKey2);

      log('Cache invalidated for:', { keys: [cacheKey1, cacheKey2].filter(Boolean) }, userSettings);
    }

    return `Memory stored: ${memory}`;
  } catch (error) {
    log('Error adding memory:', { message: error.message }, userSettings);

    return `Error storing memory: ${error.message}`;
  }
}

async function memory_plugin(params, userSettings) {
  // Start with debug info if enabled
  let debugInfo = [];
  const initialDebug = log('Memory Box plugin called:', { params }, userSettings);
  if (initialDebug) debugInfo.push(initialDebug);

  // Validate token
  if (!userSettings.token) {
    log('Missing token in userSettings', {}, userSettings);
    return "ALERT: Please go to the Memory Box settings and set your token to be able to store and retrieve memories. You can get your token from the Memory Box website at https://www.memoryplugin.com. NOTE_FOR_AI: Show this message to the user in your reply.";
  }

  // Validate required parameters based on mode
  if (!params.mode) {
    log('Missing mode parameter', {}, userSettings);
    return "Error: mode is required and can be 'search', 'store', 'load_all', or 'load_bucket'.";
  }

  // Use the bucket from params if provided, otherwise use the default from settings
  const bucketToUse = params.bucket || userSettings.bucket;

  // Check for special \m suffix in memory text
  if (params.mode === "store" && params.memory && params.memory.endsWith('\\m')) {
    params.memory = params.memory.slice(0, -2).trim();
    log('Removed \\m suffix from memory', {}, userSettings);
  }

  try {
    switch (params.mode) {
      case "search":
        if (!params.query) {
          log('Missing query parameter for search mode', {}, userSettings);
          return "Error: 'query' parameter is required when mode is 'search'.";
        }
        const searchResults = await queryMemories(params.query, userSettings);
        return searchResults.map(m => m.text);

      case "store":
        if (!params.memory) {
          log('Missing memory parameter for store mode', {}, userSettings);
          return "Error: 'memory' parameter is required when mode is 'store'.";
        }
        return await addMemory(params.memory, bucketToUse, userSettings);

      case "load_all":
        if (bucketToUse) {
          return await fetchMemoriesFromBucket(userSettings, bucketToUse);
        }
        return await fetchAllMemories(userSettings);

      case "load_bucket":
        return await fetchMemoriesFromBucket(userSettings, bucketToUse);

      default:
        log('Invalid mode', { mode: params.mode }, userSettings);
        return `Error: Invalid mode '${params.mode}'. Mode must be 'search', 'store', 'load_all', or 'load_bucket'.`;
    }
  } catch (error) {
    log('Unhandled error in memory_plugin:', { message: error.message }, userSettings);

    return `Error: ${error.message}`;
  }
}
